package com.assignments.jav5;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a =100;
		
		a+=10; //110
		System.out.println(a);
		
		a*=10; //1100
		System.out.println(a);
		
		a/=5;  //220
		System.out.println(a);
		
		a%=5;  //0
		System.out.println(a);
		
		a-=2;  //-2
		System.err.println(a);
		
		

	}

}
